import { BackgroundEnum, RaceEnum, } from "../../../../Common/RequestResponse/characterCreation";
export function turnCharacterIntoInterface(character) {
    return {
        id: character.id,
        partyID: character.partyID,
        name: character.name,
        type: character.type,
        gender: character.gender,
        portrait: character.portrait,
        background: BackgroundEnum[character.background],
        race: RaceEnum[character.race],
        alignment: character.alignment.intoInterface(),
        mood: character.mood,
        energy: character.energy,
        fame: character.fame,
        level: character.level,
        gold: character.gold,
        isDead: character.isDead,
        status: character.status.intoInterface(),
        equipment: character.equipments.intoInterface(),
        traits: character.traits.map((trait) => ({
            id: trait.id,
            name: trait.name,
            description: trait.description,
        })),
        skills: character.skills,
        activeSkills: character.activeSkills,
        position: character.position,
        itemsBag: character.itemsBag.intoInterface(),
        arcaneAptitude: character.arcaneAptitude.intoInterface(),
        bagSize: character.bagSize,
        currentHP: character.currentHP,
        currentMP: character.currentMP,
        currentSP: character.currentSP,
        maxHP: character.maxHP(),
        maxMP: character.maxMP(),
        maxSP: character.maxSP(),
    };
}
